import React, {useState} from 'react'
import Son from './Son'

export default props => {
    const [name, setName] = 'none', age = 0, employed = false

    function setAttrs(nameParam, ageParam, employedParam){
        name = nameParam
        age = ageParam
        employed = employedParam
    }

    return (
        <div>
            <p>Nome: {name}</p>
            <p>Idade: {age}</p>
            <p>Empregado: {employed ? "Sim" : "Não"}</p>
            <Son alterValues={setAttrs}></Son>
        </div>
    )
}
